/**
 * glsl files
 */
declare module '*.glsl' {
  const value: string;
  export default value;
}

/**
 * vertex files
 */
declare module '*.vs' {
  const value: string;
  export default value;
}
declare module '*.vert' {
  const value: string;
  export default value;
}

/**
 * fragment files
 */
declare module '*.fs' {
  const value: string;
  export default value;
}
declare module '*.frag' {
  const value: string;
  export default value;
}
